mapper.select:
select count(*) from hospitalinfo_s where ykiho in (select ykiho from medicalsubjectinfo_s where dgsbjtCd=#{medicalSubjectCode}) and ykiho in (select ykiho from hospitalinfo_s where
sidoCdsgguCd=#{regionCode})
and ykiho in (select ykiho from hospitalInfo_s where yadmNm like concat('%', #{yadmNm}, '%'));

select * from hospitalinfo_s where ykiho in (select ykiho from medicalsubjectinfo_s where dgsbjtCd=#{medicalSubjectCode}) and ykiho in (select ykiho from hospitalinfo_s where
sidoCdsgguCd=#{regionCode})
and ykiho in (select ykiho from hospitalInfo_s where yadmNm like concat('%', #{yadmNm}, '%')) order by yadmNm desc limit #{startIndexNo}, #{pageSize};
